import styled from '@emotion/styled';

const Button = styled.button`
  width: 100%;
  padding: 15px;
  border-radius: 8px;
  border: none;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &.primary {
    background: linear-gradient(135deg, #6C5DD3 0%, #8677E1 100%);
    color: white;
    box-shadow: 0 4px 15px rgba(108, 93, 211, 0.3);
    
    &:hover {
      background: linear-gradient(135deg, #8677E1 0%, #6C5DD3 100%);
      transform: translateY(-2px);
      box-shadow: 0 6px 20px rgba(108, 93, 211, 0.4);
    }
  }
  
  &.secondary {
    background: linear-gradient(135deg, #E8E6F9 0%, #F3F2FB 100%);
    color: #6C5DD3;
    box-shadow: 0 4px 15px rgba(108, 93, 211, 0.1);
    
    &:hover {
      background: linear-gradient(135deg, #F3F2FB 0%, #E8E6F9 100%);
      transform: translateY(-2px);
      box-shadow: 0 6px 20px rgba(108, 93, 211, 0.2);
    }
  }
`;

export default Button;
